import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";

import { Navbar, Button, Badge, Container, Dropdown } from 'react-bootstrap';
import { List, Cart3, Person } from 'react-bootstrap-icons';

import '../style/WebNav.css';

import SearchBar from "./SearchBar";
import MobileSidebar from "./Sidebar";
import MobileSearchBar from "./MobileSearchBar";
import Sidebar from "./Sidebar";

const WebNav = () => {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
            <Navbar bg="danger" variant="dark" expand="lg" className="px-2 sticky-top" >
                <Container fluid>
                    <Button variant="outline-light border border-3" onClick={handleShow}>
                        <List color="white" size={24} className='align-bottom' />
                        <strong className="px-1">Menu</strong>
                    </Button>

                    <Sidebar show={show} onHide={handleClose} />

                    <Navbar.Brand href="/" className="mx-3 fw-bold">E-Shop</Navbar.Brand>

                    {true && <MobileSearchBar />}
                    {false && <SearchBar />}

                    <Button href="/" variant="outline-light border border-3" className="mx-2 pe-0">
                        <Cart3 color="white" size={24} className='align-bottom' />
                        <Badge pill className="bg-warning counter">
                            {true && <span className="text-black">9</span>}
                        </Badge>
                        {true && <strong className="pe-3">Cart</strong>}
                    </Button>

                    <Dropdown align="end">
                        <Dropdown.Toggle variant="outline-light border border-3" id="account-submenu">
                            <Person color="white" size={24} className='align-bottom' />
                            {true && <strong className="px-1">Account</strong>}
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item href="/login">User Profile</Dropdown.Item>
                            <Dropdown.Item href="/">User Profile</Dropdown.Item>
                            <Dropdown.Item href="/">My Order</Dropdown.Item>
                            <Dropdown.Item href="/">Manage Product</Dropdown.Item>
                            <Dropdown.Item href="/">Manage Order</Dropdown.Item>
                            <Dropdown.Divider />
                            <Dropdown.Item href="/" className="text-danger">Logout</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>

                </Container>
            </Navbar>
        </>
    )
}

export default WebNav;
