import React, { useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { Container, Col, Row, Button, Form, InputGroup } from 'react-bootstrap';
import { ArrowReturnLeft } from "react-bootstrap-icons";
import '../style/Order.css';

import { order } from "../data/order";

const OrderDetail = () => {
    const { userid } = useParams();
    const userOrder = order.filter(item => (item.id === userid))[0];

    return (
        <Container className="mt-4">
            <div className='row justify-content-center'>
                <h1 className="text-center mb-3">Order Details</h1>
                <Link to='/order'><ArrowReturnLeft className="me-2" />Return to Order</Link>
            </div>

        </Container>
    )
}

export default OrderDetail;
