import React from "react";
import { product} from '../data/product';
import '../style/ProductList.css';

const ProductList = () => {
    return product.map((prod, idx) => (
        <div className="product-box border">
            <img src={prod.coverImg} alt={prod.title} 
            className="product-img mx-auto"/>
            <p className="fw-bold text-center">{prod.title}</p>            
        </div>
    ))
}

export default ProductList;
