import React, { useState } from "react";
import { orderData } from '../data/cartData';


const Cart = () => {
    const [orderList, setOrderList] = useState(orderData);

    return (
        <>
            <h1>Cart</h1>
            {
                orderList.map((item, idx) => (
                    <div className="product-container border">
                        <div>
                            <img src={item.sku.img} alt={item.sku.itemName}/>
                        </div>
                        <div>
                            <h2>
                                {item.title}
                            </h2>
                            <h3>
                                {item.sku.itemName}
                            </h3>
                        </div>
                        <div>
                            {item.sku.price}
                            {item.sku.price * (100 - item.discount) / 100}
                        </div>
                        <div>
                            {item.amount}
                        </div>
                        <div>
                            {item.sku.price * item.amount}
                        </div>
                    </div>
                ))
            }


        </>
    )
}

export default Cart;
