import React, { useEffect } from "react";
import { Container, Row, Col, Badge, Card, Button, Form, InputGroup } from "react-bootstrap";
import { Formik, useFormikContext } from 'formik';
import * as Yup from 'yup';
import '../style/Checkout.css';

const AutoSubmitToken = () => {
    // Grab values and submitForm from context
    const { values, setFieldValue } = useFormikContext();
    useEffect(() => {
        if (values.payment !== 'Credit Card') {
            setFieldValue(values.cardNumber,'');
            setFieldValue(values.cardHolder,'');
            setFieldValue(values.expiryDate,'');
            setFieldValue(values.cvv,'');
        }
    }, [values, setFieldValue]);
    return null;
  };


const schema = Yup.object().shape({
    name: Yup.string().required('Your name is missing.'),
    phone: Yup.string().matches(/^\d{8}$/, 'Invalid Phone Number').required('Phone number is missing.'),
    address: Yup.string()
        .when('payment', {
            is: (payment) => payment !== 'Cash',
            then: Yup.string().required('Shipping address is missing.')

        }),
    cardNumber: Yup.string()
        .when('payment', {
            is: (payment) => payment === 'Credit Card',
            then: Yup.string().matches(/^\d{4} \d{4} \d{4} \d{4}$/, 'Invalid card number').required('Card number is missing.')

        }),
    cardHolder: Yup.string()
        .when('payment', {
            is: (payment) => payment === 'Credit Card',
            then: Yup.string().required('Card holder\'s name is missing.')

        }),
    expiryDate: Yup.string()
        .when('payment', {
            is: (payment) => payment === 'Credit Card',
            then: Yup.string().matches(/^\d{2}\/\d{2}$/, 'Invalid expiry date format').required('Expiry date is missing.')

        }),
    cvv: Yup.string()
        .when('payment', {
            is: (payment) => payment === 'Credit Card',
            then: Yup.string().length(3, 'Invalid CVV').required('CVV number is missing.')

        }),
    notes: Yup.string().max(100, "Maximum 100 characters allowed.")
});

const initialize = {
    name: '',
    phone: '',
    address: '',
    payment: 'Cash',
    cardNumber: '',
    cardHolder: '',
    expiryDate: '',
    cvv: '',
    notes: '',
}

const CheckoutPage = () => {

    useEffect(() => {
        if (payment !== 'Credit Card') {
          setFieldValue(cardNumber, '');
          setFieldValue(cardHolder, '');
          setFieldValue(expiryDate, '');
          setFieldValue(cvv, '');
        }
      }, [payment]);
    return (
        <>
            <Container id='checkout-container' className="mt-3 mb-5">
                <div className="py-4 text-center">
                    <h2>Checkout</h2>
                </div>

                <Row className="g-5">
                    <Col md={{ span: 5, order: "last" }}>
                        <h4 className="d-flex justify-content-between align-items-center mb-3">
                            <span className="text-danger">Your cart</span>
                            <Badge pill bg="danger">9</Badge>
                        </h4>

                        <ul className="list-group mb-3">
                            <li className="list-group-item d-flex justify-content-between lh-sm">
                                <div>
                                    <h6 className="my-0">Product name</h6>
                                    <small className="text-muted">Brief description</small>
                                </div>
                                <span className="text-muted">$12</span>
                            </li>
                        </ul>
                    </Col>

                    <Col md={7}>
                        <Formik
                            initialValues={initialize}
                            validationSchema={schema}
                            onSubmit={(values) => {
                                alert(JSON.stringify(values, null, 2));
                            }}
                        >
                            {
                                ({ values, errors, touched, handleSubmit, handleChange, handleBlur }) => (
                                    <>
                                        <h4 className="mb-3">Billing address</h4>
                                        <Form id='checkout-form' noValidate onSubmit={handleSubmit}>
                                            <Row className="g-3">
                                                <Form.Group as={Col} md="7" controlId="name">
                                                    <Form.Label>Name</Form.Label>
                                                    <Form.Control
                                                        type="text"
                                                        name="name"
                                                        value={values.name}
                                                        onChange={handleChange}
                                                        onBlur={handleBlur}
                                                        isInvalid={touched.name && !!errors.name}
                                                    />
                                                    <Form.Control.Feedback type="invalid">
                                                        {errors.name}
                                                    </Form.Control.Feedback>
                                                </Form.Group>

                                                <Form.Group as={Col} md="5" controlId="phone">
                                                    <Form.Label>Phone Number</Form.Label>
                                                    <InputGroup>
                                                        <InputGroup.Text id="telCode">+852</InputGroup.Text>
                                                        <Form.Control
                                                            type="tel"
                                                            name="phone"
                                                            inputMode="tel"
                                                            maxLength="8"
                                                            aria-describedby="telCode"
                                                            value={values.phone.replace(/[^0-9]/g, '')}
                                                            onChange={handleChange}
                                                            onBlur={handleBlur}
                                                            isInvalid={touched.phone && !!errors.phone}
                                                        />
                                                        <Form.Control.Feedback type="invalid">
                                                            {errors.phone}
                                                        </Form.Control.Feedback>
                                                    </InputGroup>
                                                </Form.Group>

                                                <Form.Group as={Col} md="12" controlId="address">
                                                    <Form.Label>Address</Form.Label>
                                                    <Form.Control
                                                        type="text"
                                                        name="address"
                                                        value={values.address}
                                                        onChange={handleChange}
                                                        onBlur={handleBlur}
                                                        isInvalid={touched.address && !!errors.address}
                                                    />
                                                    <Form.Control.Feedback type="invalid">
                                                        {errors.address}
                                                    </Form.Control.Feedback>
                                                </Form.Group>

                                            </Row>

                                            <hr className="my-4" />
                                            <h4 className="mb-3">Payment</h4>

                                            <div role="group" aria-labelledby="payment-group" className="my-3">
                                                <Form.Check
                                                    type="radio"
                                                    name="payment"
                                                    id="cash"
                                                    label="Cash"
                                                    value='Cash'
                                                    defaultChecked
                                                    onChange={handleChange}
                                                />
                                                <Form.Check
                                                    type="radio"
                                                    name="payment"
                                                    id="credit-card"
                                                    label="Credit Card"
                                                    value='Credit Card'
                                                    onChange={handleChange}

                                                />
                                                <Form.Check
                                                    type="radio"
                                                    name="payment"
                                                    id="bank-transfer"
                                                    label="Bank Transfer"
                                                    value="Bank Transfer"
                                                    onChange={handleChange}
                                                />
                                            </div>

                                            <div className={values.payment !== 'Credit Card' ? "mb-3" : "mb-3 d-none"}>
                                                <strong>Cash: </strong>Pickup at store only
                                                <br />
                                                <strong>Pickup Location: </strong> Unit 1, 1/F, ABC Building, Hong Kong, N.T.
                                                <br /><br />
                                                <strong>Bank Transfer: </strong> Please transfer to the account
                                                <br />
                                                <strong>Bank Acc.: </strong> 000-000-0-000000-0 (E-shop Company)
                                                <br /> <br />
                                                Once the transfer completed, please send the capture of transaction record to
                                                <br />
                                                <strong>Whatsapp: </strong>+852 0000 0000
                                                <br />
                                                After the payment confirmed, we will arrange the delivery.
                                            </div>

                                            <div className={values.payment === 'Credit Card' ? "mb-3" : "mb-3 d-none"}>
                                                <Row className="g-3">
                                                    <Form.Group controlId="card-no">
                                                        <Form.Label>Credit Card Number</Form.Label>
                                                        <Form.Control
                                                            type="tel"
                                                            name="cardNumber"
                                                            inputMode="numeric"
                                                            placeholder="0000 0000 0000 0000"
                                                            maxLength="19"
                                                            value={values.cardNumber
                                                                .replace(/[^0-9]/g, '')
                                                                .replace(/\s/g, "")
                                                                .replace(/(\d{4})/g, "$1 ")
                                                                .trim()}
                                                            onChange={handleChange}
                                                            onBlur={handleBlur}
                                                            isInvalid={touched.cardNumber && !!errors.cardNumber}
                                                        />
                                                        <Form.Control.Feedback type="invalid">
                                                            {errors.cardNumber}
                                                        </Form.Control.Feedback>
                                                    </Form.Group>

                                                    <Form.Group controlId="card-name">
                                                        <Form.Label>Card Holder Name</Form.Label>
                                                        <Form.Control
                                                            type="text"
                                                            name="cardHolder"
                                                            value={values.cardHolder}
                                                            onChange={handleChange}
                                                            onBlur={handleBlur}
                                                            isInvalid={touched.cardHolder && !!errors.cardHolder}
                                                        />
                                                        <Form.Control.Feedback type="invalid">
                                                            {errors.cardHolder}
                                                        </Form.Control.Feedback>
                                                    </Form.Group>

                                                    <Form.Group as={Col} md="6" controlId="expiry-date">
                                                        <Form.Label>Expiry Date (MM/YY)</Form.Label>
                                                        <Form.Control
                                                            type="text"
                                                            name="expiryDate"
                                                            placeholder="MM/YY"
                                                            pattern="\d\d/\d\d"
                                                            value={values.expiryDate
                                                                .replace(/[^0-9]/g, '')
                                                                .replace(/\s/g, "")
                                                                .replace(/^(\d{2})/, "$1/")}
                                                            maxLength="5"
                                                            onChange={handleChange}
                                                            onBlur={handleBlur}
                                                            isInvalid={touched.expiryDate && !!errors.expiryDate}
                                                        />
                                                        <Form.Control.Feedback type="invalid">
                                                            {errors.expiryDate}
                                                        </Form.Control.Feedback>
                                                    </Form.Group>

                                                    <Form.Group as={Col} md="6" controlId="cvv">
                                                        <Form.Label>CVV</Form.Label>
                                                        <Form.Control
                                                            type="tel"
                                                            inputMode="numeric"
                                                            name="cvv"
                                                            placeholder="CVV"
                                                            pattern="\d{3}"
                                                            maxLength="3"
                                                            value={values.cvv}
                                                            onChange={handleChange}
                                                            onBlur={handleBlur}
                                                            isInvalid={touched.cvv && !!errors.cvv}
                                                        />
                                                        <Form.Control.Feedback type="invalid">
                                                            {errors.cvv}
                                                        </Form.Control.Feedback>
                                                    </Form.Group>
                                                </Row>

                                            </div>

                                            <Form.Group className="mb-3" controlId="checkout-notes">
                                                <Form.Label>Notes</Form.Label>
                                                <Form.Control
                                                    as="textarea"
                                                    rows={3}
                                                    name="notes"
                                                    value={values.notes}
                                                    onChange={handleChange}
                                                    isInvalid={!!errors.notes}
                                                    maxLength="100" />
                                                <Form.Text className="text-secondary">
                                                    {`Remaining Characters: ${100 - values.notes.length}`}
                                                </Form.Text>
                                            </Form.Group>

                                            <Card className="p-2">
                                                <Button variant="primary" type="submit">Submit Order</Button>
                                            </Card>
                                        </Form>
                                    </>
                                )
                            }

                        </Formik>
                    </Col>
                </Row>

            </Container>
        </>
    )
}

export default CheckoutPage;
