import React from 'react';
import { Form, Buttonm, InputGroup } from 'react-bootstrap';
import { Link } from 'react-router-dom';

import '../style/login.css'


const LoginForm = () => {
  return (
    <div className='border border-danger border-2 rounded-3 login-form form-signin'>
      <Form>
        <h1 className='h2 fw-bold text-center mb-4'>Login</h1>
        <Form.Group className="form-floating" controlId="inputEmailAndUser">
          <Form.Control type="text" placeholder="Username or Email Address"/>
          <Form.Label>Username or Email Address</Form.Label>
        </Form.Group>

        <Form.Group className="form-floating" controlId="inputPassword">
          <Form.Control type="password" placeholder="Password"/>
          <Form.Label>Password</Form.Label>
        </Form.Group>

        <div className='d-flex justify-content-between mt-4'>
          <Link to='/register'>
            <Button variant="btn btn-link ">Register</Button>
          </Link>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </div>


      </Form>
    </div>
  )
};

export default LoginForm;
