import React from "react";
import { useState } from "react";
import { Formik } from 'formik';
import '../style/Login.css'

const schema = Yup.object().shape({
  name: Yup.string().required('Your name is missing.'),
  phone: Yup.string().matches(/^\d{8}$/,'Invalid Phone Number').required('Phone number is missing.'),
  address: Yup.string().required('Shipping address is missing.'),
  notes: Yup.string().max(100, "Maximum 100 characters.")
});

const Login = () => {

  return (
    <div id='login-form' className="form-signin">

    </div>
  );
}

export default Login;
