import React from 'react';
import { Form, Button, InputGroup, FloatingLabel } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { Eye, EyeSlash } from 'react-bootstrap-icons';

const LoginForm = (props) => {
  return (
    <Form onSubmit={props.onSubmit}>
      <h1 className='fs-2 fw-bold mb-4 text-center py-2 bg-warning text-white'>Login</h1>

      <FloatingLabel
        controlId="inputEmailAndUser"
        label="Username or Email Address"
      >
        <Form.Control
          type="text"
          placeholder="Username or Email Address"
          name='userInfo'
          value={props.inputs.userInfo || ''}
          onChange={props.onInputChange}
          required />
      </FloatingLabel>

      <InputGroup>
        <FloatingLabel controlId="inputPassword" label="Password" className='col-12 '>
          <Form.Control
            type={props.showPassword ? "text" : "password"}
            placeholder="Password"
            className='pe-5'
            name='password'
            value={props.inputs.password || ''}
            onChange={props.onInputChange}
            required />
        </FloatingLabel>
        <Button id='toggle-password' className='border-0 bg-transparent' onClick={props.onToggle}>
          {!props.showPassword && <Eye color='black' size={24} />}
          {props.showPassword && <EyeSlash color='black' size={24} />}
        </Button>
      </InputGroup>

      <div className='d-flex justify-content-between mt-4'>
        <Link to='/register'>
          <Button variant="btn btn-link ps-0">Register</Button>
        </Link>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </div>
    </Form>
  )
};

export default LoginForm;
