import React from "react";
import { useState } from "react";
import { Formik } from 'formik';
import * as Yup from 'yup';
import { Form, FloatingLabel, InputGroup } from "react-bootstrap";
import '../style/Login.css'
import PasswordVisible from "../component/PasswordVisible";

const schema = Yup.object().shape({
  user: Yup.string().required('Please enter username or email.'),
  password: Yup.string().required('Please enter password.')
});

const Login = () => {
  const [showPassword, setShowPassword] = useState({
    passwordBtn: false
  });

  const togglePassword = (id) => {
    setShowPassword(prevState => ({ ...prevState, [id]: !prevState[id] }))
  };

  return (
    <div id='login-form' className="form-signin">
      <Formik
        initialValues={{
          user: '',
          password: ''
        }}
        validationSchema={schema}

        onSubmit={(values) => {
          alert(JSON.stringify(values, null, 2));
        }}
      >
        {
          ({ values, errors, handleSubmit, handleChange }) => (
            <Form onSubmit={handleSubmit}>
              <h1 className='fs-2 fw-bold mb-4 text-center py-2'>Login</h1>

              <FloatingLabel
                controlId="inputEmailAndUser"
                label="Username or Email Address"
              >
                <Form.Control
                  type="text"
                  placeholder="Username or Email Address"
                  name="user"
                  value={values.user}
                  onChange={handleChange} />
              </FloatingLabel>

              <InputGroup>
                <FloatingLabel controlId="inputPassword" label="Password" className='col-12 '>
                  <Form.Control
                    type={props.showPassword ? "text" : "password"}
                    placeholder="Password"
                    className='pe-5'
                    name='password'
                    value={props.inputs.password || ''}
                    onChange={props.onInputChange}
                    required />
                </FloatingLabel>
                <Button id='toggle-password' className='border-0 bg-transparent' onClick={props.onToggle}>
                  {!props.showPassword && <Eye color='black' size={24} />}
                  {props.showPassword && <EyeSlash color='black' size={24} />}
                </Button>
              </InputGroup>

            </Form>
          )
        }


      </Formik>

    </div>
  );
}

export default Login;
