import React from 'react';
import { Container, Row, Col, Button, Form } from 'react-bootstrap';
import { Formik, FieldArray } from 'formik';
import * as Yup from 'yup';
import '../style/ManageProduct.css'


const initialize = {
    code: '',
    title: '',
    description: '',
    coverImg: '',
    category: 'Rice',
    sku: [{
        itemName: '',
        price: 0,
        unit: '',
        inStock: 0,
        img: '',
        discount: 0,
    }]

}


const CreateProduct = () => {
    return (
        <Container id='create-prod-form'>
            <div className="py-4 text-center">
                <h1>Create Product</h1>
            </div>
            <Row className='g-5'>
                <Formik
                    initialValues={initialize}
                    onSubmit={(values) => {
                        alert(JSON.stringify(values, null, 2));
                    }}
                >
                    {
                        ({ values, errors, touched, handleSubmit, handleChange, handleBlur, resetForm, setFieldValue }) => (
                            <Form onSubmit={handleSubmit}>
                                <Row className="g-3 mb-3">
                                    <Form.Group as={Col} md="2">
                                        <Form.Label>Code</Form.Label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Code"
                                            name="code"
                                            value={values.code}
                                            onChange={handleChange}
                                        />
                                    </Form.Group>

                                    <Form.Group as={Col} md='2'>
                                        <Form.Label>Category</Form.Label>
                                        <Form.Select
                                            aria-label="Search Term"
                                            name='category'
                                            value={values.category}
                                            onChange={handleChange}>
                                            <option value="Rice">Rice</option>
                                            <option value="Noodles">Noodles</option>
                                            <option value="Oil">Oil</option>
                                        </Form.Select>
                                    </Form.Group>

                                    <Form.Group as={Col} md="8">
                                        <Form.Label>Product Title</Form.Label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Product Title"
                                            name="title"
                                            value={values.title}
                                            onChange={handleChange}
                                        />
                                    </Form.Group>


                                </Row>

                                <Form.Group className='mb-3'>
                                    <Form.Label>Cover Image URL</Form.Label>
                                    <Form.Control
                                        type="text"
                                        placeholder="Cover Image URL"
                                        name="coverImg"
                                        value={values.coverImg}
                                        onChange={handleChange}
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3">
                                    <Form.Label>Product Description</Form.Label>
                                    <Form.Control
                                        as="textarea"
                                        rows={4}
                                        name="description"
                                        value={values.description}
                                        onChange={handleChange}
                                        maxLength="500" />
                                    <Form.Text className="text-secondary">
                                        {`Remaining Characters: ${500 - values.description.length}`}
                                    </Form.Text>
                                </Form.Group>
                                <div className=''>
                                    <h2>Items</h2>
                                    <FieldArray
                                        name="sku"
                                        render={
                                            ({ remove, push }) => (
                                                <div>
                                                    {
                                                        values.sku.length > 0 &&
                                                        values.sku.map((item, index) => (
                                                            <Row key={index}>
                                                                <Form.Group as={Col} md='8'>
                                                                    <Form.Label>Item Title</Form.Label>
                                                                    <Form.Control
                                                                        type="text"
                                                                        name={`sku[${index}].itemName`}
                                                                        value={values.sku[index].itemName}
                                                                        onChange={handleChange}
                                                                    />
                                                                </Form.Group>
                                                            </Row>
                                                        ))
                                                    }
                                                </div>
                                            )
                                        }
                                    />
                                </div>

                            </Form>
                        )
                    }

                </Formik>
            </Row>
        </Container >
    )

}

export default CreateProduct;
