import React from "react";
import { useState } from "react";
import { Formik } from 'formik';
import * as Yup from 'yup';
import { Form, FloatingLabel, InputGroup, Button } from "react-bootstrap";
import '../style/Login.css'
import PasswordVisible from "../component/PasswordVisible";

const schema = Yup.object().shape({
  user: Yup.string().required('Please enter username or email.'),
  password: Yup.string().required('Please enter password.')
});

const Login = () => {
  const [showPassword, setShowPassword] = useState({
    passwordBtn: false
  });

  const togglePassword = (id) => {
    console.log(showPassword);
    setShowPassword(prevState => ({ ...prevState, [id]: !prevState[id] }))
  };

  return (
    <div id='login-form' className="form-signin">
      <Formik
        initialValues={{
          user: '',
          password: ''
        }}
        validationSchema={schema}

        onSubmit={(values) => {
          alert(JSON.stringify(values, null, 2));
        }}
      >
        {
          ({ values, errors, handleSubmit, handleChange }) => (
            <Form onSubmit={handleSubmit}>
              <h1 className='fs-2 fw-bold mb-4 text-center py-2'>Login</h1>

              <FloatingLabel
                controlId="inputEmailAndUser"
                label="Username or Email Address"
              >
                <Form.Control
                  type="text"
                  placeholder="Username or Email Address"
                  name="user"
                  value={values.user}
                  onChange={handleChange} />
              </FloatingLabel>

              <InputGroup>
                <FloatingLabel controlId="inputPassword" label="Password" className='col-12 '>
                  <Form.Control
                    type={showPassword.passwordBtn ? "text" : "password"}
                    placeholder="Password"
                    className='pe-5'
                    name='password'
                    value={values.password}
                    onChange={handleChange}
                  />
                </FloatingLabel>
                <PasswordVisible
                  id='toggle-password'
                  name='passwordBtn'
                  onToggle={togglePassword}
                  showPassword={showPassword.passwordBtn}
                />
              </InputGroup>

              <div className='d-flex justify-content-between mt-4'>
                <Link to='/login'>
                  <Button variant="btn btn-link ps-0">Login</Button>
                </Link>
                <Button variant="primary" type="submit">
                  Submit
                </Button>
              </div>

            </Form>
          )
        }


      </Formik>

    </div>
  );
}

export default Login;
