import React from "react";
import Footer from "../component/Footer";
import WebNav from "../component/WebNav";
import RegisterForm from "../component/RegisterForm";
import { useState } from 'react';
import '../style/register.css'

const Register = () => {

    const [showPassword, setShowPassword] = useState({
        passwordBtn: false,
        confirmBtn: false
    });

    const [inputs, setInputs] = useState(
        {
            username: '',
            email: '',
            confirmEmail: '',
            password: '',
            confirmPassword: ''
        }
    );

    const togglePassword = (id) => {
        setShowPassword(prevState => ({ ...prevState, [id]: !prevState[id] }))
    };

    const validateForm = {
        checkUsernameFormat: () => {
            const usernameFormat = /^[\w-]+$/;
            return usernameFormat.test(inputs.username);
        },
        checkUsernameLength: () => {
            return inputs.username.length >= 6 && inputs.username.length <= 20;
        },
        checkEmailMatch: () => {
            return inputs.email === inputs.confirmEmail;
        },
        checkPasswordFormat: () => {
            return inputs.password.length >= 6 && inputs.password.length <= 20;
        },
        checkPasswordMatch: () => {
            return inputs.password === inputs.confirmPassword;
        }
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        alert(JSON.stringify(inputs));
    };

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({ ...values, [name]: value }))
    };

    return (
        <div className="d-flex flex-column justify-content-between min-vh-100">
            <WebNav />
            <div id='reg-form-container' className="border border-danger border-2 rounded-3">
                <RegisterForm
                    inputs={inputs}
                    onInputChange={handleChange}
                    onValidation={validateForm}
                    showPassword={showPassword}
                    onToggle={togglePassword}
                    onSubmit={handleSubmit} />
            </div>
            <div className="">
                <Footer />
            </div>
        </div>
    )
};

export default Register;
