import React from 'react';
import { Form, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const LoginForm = () => {
  return (
    <div className='border border-danger border-2 rounded-3 p-3'>
      <Form>
        <h1 className='h2 fw-bold text-center mb-3'>Login</h1>
        <Form.Group className="mb-3" controlId="input-email-user">
          <Form.Label>Username or Email address</Form.Label>
          <Form.Control type="text" />
        </Form.Group>

        <Form.Group className="mb-3" controlId="input-password">
          <Form.Label>Password</Form.Label>
          <Form.Control type="text" />
        </Form.Group>

        <div className='d-flex justify-content-between'>
          <Link to='/register'>
            <Button variant="btn btn-link p-1">Register</Button>
          </Link>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </div>


      </Form>
    </div>
  )
};

export default LoginForm;
