import React, { useState } from 'react';
import { OverlayTrigger, Popover, Button, Form, FormControl, Navbar } from 'react-bootstrap';
import { Search, XLg } from 'react-bootstrap-icons';
import '../style/MobileSearchBar.css'

const MobileSearchBar = () => {
    const [triggered, setTriggered] = useState(false);
    const handleTrigger = () => setTriggered(!triggered);

    return (
        <>
            <div className="me-auto" />
            <Button variant="outline-light border border-3" onClick={handleTrigger}>
                {!triggered && <Search color="white" size={24} />}
                {triggered && <XLg color="white" size={24} />}
            </Button>
            {triggered &&
                    <Form id='search-bar' className="d-flex position-absolute top-100 my-2 p-2 w-100 shadow-lg">
                        <FormControl
                            type="search"
                            placeholder="Search Product"
                            className="me-2"
                            aria-label="Search"
                        />
                        <Button variant="outline-light border border-3">
                            <Search className='text-danger' size={24} />
                        </Button>
                    </Form>

            }
        </>
    )
};

export default MobileSearchBar;
