import React from "react";

const Products = (props) => {
    const noImagePath = '/img/no_image.svg';

    return (
        <div className='mt-4 mx-5 row'>
            <div id='product-img' className="border border-secondary col-md-4">
                <img src={props.product.sku[0].img || noImagePath}
                    alt={props.product.sku[0].item} />
            </div>
            <div id='product-info' className="border border-secondary col-md">
                <h1>{props.product.title}</h1>
                {props.product.sku[1].discount > 0 ?
                <h2><del>${props.product.sku[1].price}</del>
                    ${props.product.sku[1].price * (100 - props.product.sku[1].discount) /100}</h2>:
                <h2>${props.product.sku[1].price}</h2>
                }

            <p>{props.product.description}</p>
            <button className="btn btn-primary">-</button>
            <span className="mx-2">number</span>
            <button className="btn btn-primary">+</button>
            <span className="mx-2">{props.product.sku[0].unit || 'Pieces'}</span>
            <br />
            <button className="btn btn-primary">Add to Cart</button>

        </div>
        </div >
    )
}

export default Products;
