import React from "react";

import { Link } from 'react-router-dom';

import { Dropdown, NavDropdown } from 'react-bootstrap';
import { Person } from 'react-bootstrap-icons';

const AccountBtn = () => {
    return (
        <Dropdown align='end'>
            <Dropdown.Toggle variant="outline-light border border-3 nav-btn" id="account-submenu">
                <Person color="white" size={24} className='align-bottom' />
                <span id='acc-text' className="px-1 fw-bold">Account</span>
            </Dropdown.Toggle>

            <Dropdown.Menu>
                <Link to="/user/login" className="dropdown-item text-primary">Login</Link>
                <Dropdown.Divider />
                <Link to="/user/modify-password" className="dropdown-item">Modify Password</Link>
                <Link to="/" className="dropdown-item">My Order</Link>
                <Link to="/product/manage" className="dropdown-item">Manage Product</Link>
                <Link to="/" className="dropdown-item">Manage Order</Link>
                <Dropdown.Divider />
                <Link to="/" className="dropdown-item text-danger">Logout</Link>
            </Dropdown.Menu>
        </Dropdown >
    )
}

export default AccountBtn;
