import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";

import { Navbar, Button, Badge, Container} from 'react-bootstrap';
import { List, Cart3 } from 'react-bootstrap-icons';

import '../style/WebNav.css';

import SearchBar from "./Navbar/SearchBar";
import MobileSearchBar from "./Navbar/MobileSearchBar";
import Sidebar from "./Navbar/Sidebar";
import AccountBtn from "./Navbar/AccountBtn";

const WebNav = () => {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
            <Navbar bg="danger" variant="dark" expand="lg" className="px-2 sticky-top" >
                <Container fluid>
                    <Button variant="outline-light border border-3 nav-btn" onClick={handleShow}>
                        <List color="white" size={24} className='align-bottom' />
                        <strong className="px-1">Menu</strong>
                    </Button>

                    <Sidebar show={show} onHide={handleClose} />

                    <Link to='/' className="mx-3 fw-bold navbar-brand">E-Shop</Link>

                    {show && <MobileSearchBar />}
                    {!show && <SearchBar />}

                    <Link to="/login">
                        <Button variant="outline-light border border-3 nav-btn" className="mx-2 pe-0">
                            <Cart3 color="white" size={24} className='align-bottom' />
                            <Badge pill className="bg-warning counter">
                                {true && <span className="text-black">9</span>}
                            </Badge>
                            {true && <strong className="pe-3">Cart</strong>}
                        </Button>
                    </Link>
                    
                    <AccountBtn />


                </Container>
            </Navbar>
        </>
    )
}

export default WebNav;
