import React, { useState } from "react";
import { orderData } from '../data/cartData';
import { Trash } from 'react-bootstrap-icons';
import '../style/Cart.css'


const Cart = () => {
    const [orderList, setOrderList] = useState(orderData);

    return (
        <>
            <h1>Cart</h1>
            <div id="cart-container" className="mx-auto my-2 container">
                <div id="cart-header" className="row">
                    <div className="col-md-2">Product</div>
                    <div className="col-md-2">Unit Price</div>
                    <div className="col-md-2">Quantity</div>
                    <div className="col-md-3">Sub-Total</div>
                </div>
                <div id="product-info" className="col-md-5">
                    {
                        orderList.map((item, idx) => (
                            <div className="product-container border my-3">
                                <div>
                                    <img src={item.sku.img} alt={item.sku.itemName}
                                    className="cart-prod-img" />
                                </div>
                                <div>
                                    <h2>
                                        {item.title}
                                    </h2>
                                    <h3>
                                        {item.sku.itemName}
                                    </h3>
                                </div>
                                <div>
                                    {item.sku.price} <br />
                                    {item.sku.price * (100 - item.sku.discount) / 100}
                                </div>
                                <div>
                                    <button className="btn btn-primary btn-sm">-</button>
                                    <div className="d-inline-block mx-3 text-center">{item.amount}</div>
                                    <button className="btn btn-primary btn-sm">+</button>
                                </div>
                                <div>
                                    {item.sku.price * (100 - item.sku.discount) / 100 * item.amount}
                                </div>
                                <button className="btn btn-danger btn-sm"><Trash size={16} /></button>
                            </div>
                        ))
                    }
                </div>
                <div id="product-summary" className="border col-md-auto">
                    <h2>Items:</h2>
                    <h3>
                        {orderList.length}
                    </h3>
                    <h2>Total Price:</h2>
                    <h3>
                        {
                            orderList.reduce((prevVal, currValue) => (
                                prevVal +
                                currValue.amount *
                                (currValue.sku.price * (100 - currValue.sku.discount) / 100)
                            ), 0)
                        }
                    </h3>
                </div>
            </div>


        </>
    )
}

export default Cart;
