import React, { useState } from "react";
import { Container, Col, Row, Button, Form, InputGroup } from 'react-bootstrap';
import { EyeFill } from "react-bootstrap-icons";
import { Link, useParams } from "react-router-dom";

import '../style/Order.css'
import { order } from "../data/order";

const statusColor = (status) => {
    switch (status) {
        case 'Pending': return 'text-warning';
        case 'Delivering': return 'text-info';
        case 'Completed': return 'text-success';
        case 'Cancelled': return 'text-danger';
        default: return 'text-black';
    }
}

const ManageOrder = () => {
    const { userid } = useParams();
    const [startDate, setStartDate] = useState('1990-01-01');
    const [endDate, setEndDate] = useState(new Date().toISOString().split('T')[0]);

    const [user, setUser] = useState('');
    const [status, setStatus] = useState('Any');

    const [sort, setSort] = useState('asc-id');

    const handleUser = (event) => {
        setUser(event.target.value);
    }

    const handleStatus = (event) => {
        setStatus(event.target.value);
    }

    const handleStartDate = (event) => {
        setStartDate(event.target.value);
    }

    const handleEndDate = (event) => {
        setEndDate(event.target.value);
    }

    const handleSort = (event) => {
        setSort(event.target.value);
    }


    return (
        <Container id='manage-order-container' className="mt-4">
            <h1 className="text-center mb-3">Manage Order</h1>
            <Row className="mx-1 mb-3">
                <Form.Group as={Col} className="px-1">
                    <InputGroup>
                        <InputGroup.Text>User</InputGroup.Text>
                        <Form.Control
                            type="text"
                            placeholder="Search User"
                            value={user}
                            onChange={handleUser} />
                    </InputGroup>
                </Form.Group>
            </Row>

            <Row className="mx-1 mb-3">
                <Form.Group as={Col} xs='12' sm md className="px-1">
                    <InputGroup>
                        <InputGroup.Text>Start</InputGroup.Text>
                        <Form.Control
                            type="date"
                            placeholder="Start"
                            value={startDate}
                            onChange={handleStartDate} />
                    </InputGroup>
                </Form.Group>
                <Form.Group as={Col} xs='12' sm md className="px-1 mt-3 mt-sm-0">
                    <InputGroup>
                        <InputGroup.Text>End</InputGroup.Text>
                        <Form.Control
                            type="date"
                            placeholder="End"
                            value={endDate}
                            onChange={handleEndDate} />
                    </InputGroup>
                </Form.Group>

                <Form.Group as={Col} sm='12' md className="px-1 mt-3 mt-md-0">
                    <InputGroup>
                        <InputGroup.Text>Status</InputGroup.Text>
                        <Form.Select aria-label="Search Term" value={status} onChange={handleStatus}>
                            <option value="Any">Any</option>
                            <option value="Pending">Pending</option>
                            <option value="Delivering">Delivering</option>
                            <option value="Completed">Completed</option>
                            <option value="Cancelled">Cancelled</option>
                        </Form.Select>
                    </InputGroup>
                </Form.Group>
            </Row>

            <Row className="mx-1 mb-3">
                <Form.Group className="px-1">
                    <InputGroup>
                        <InputGroup.Text>Sorting</InputGroup.Text>
                        <Form.Select aria-label="Search Term" value={sort} onChange={handleSort}>
                            <option value="asc-id">Smallest ID First</option>
                            <option value="desc-id">Greatest ID First</option>
                            <option value="asc-time">Earliest Time First</option>
                            <option value="desc-time">Latest Time First</option>
                        </Form.Select>
                    </InputGroup>
                </Form.Group>
            </Row>

            {
                order
                    .filter((info) => {
                        return info.user.includes(user) &&
                            info.time >= new Date(startDate) && info.time <= new Date(endDate) &&
                            (status !== 'Any' ? info.status === status : true)
                    })
                    .sort((a, b) => {
                        switch (sort) {
                            case ("asc-id"): return a.id - b.id;
                            case ("desc-id"): return b.id - a.id;
                            case ("asc-time"): return a.time - b.time;
                            case ("desc-time"): return b.time - a.time;
                            default: return a.id - b.id;

                        }
                    })
                    .map((info, index) => (
                        <Row className="border mb-3 mx-1 py-2" key={index}>
                            <div className="d-flex justify-content-between mb-2 flex-wrap">
                                <u>{`id: ${info.id || index}`}</u>
                                <div>{info.time.toLocaleString('en-GB', { hour12: false })}</div>
                            </div>
                            <Row>
                                <Col >{info.user}</Col>
                                <Col className="text-center">$
                                    {
                                        info.product.reduce((prevVal, currValue) => (
                                            prevVal +
                                            currValue.amount *
                                            (currValue.sku.price * (100 - currValue.sku.discount) / 100)
                                        ), 0)
                                    }
                                </Col>
                                <Col className="text-end text-sm-center ">
                                    <div className={statusColor(info.status)}>{info.status}</div>
                                </Col>
                                <Col xs={12} sm>
                                    <Link to={`/order/${info.id}`} >
                                        <Button size='sm' variant='secondary' className="float-end view-order-btn">
                                            <EyeFill color="white" size={16} className='align-middle' />
                                            <span className="px-1 fw-bold">View</span>
                                        </Button>
                                    </Link>
                                </Col>
                            </Row>
                        </Row>
                    ))
            }

        </Container >
    )
}

export default ManageOrder;
