import React from "react";
import Footer from "../component/Footer";
import WebNav from "../component/WebNav";
import RegisterForm from "../component/RegisterForm";
import { useState } from 'react';

const Register = () => {

    const [showPassword, setShowPassword] = useState({
        passwordBtn: false,
        confirmBtn: false
    });

    const [inputs, setInputs] = useState(
        {
            username: '',
            email: '',
            confirmEmail: '',
            password: '',
            confirmPassword: ''
        }
    );

    const togglePassword = (id) => {
        console.log([id]);
        setShowPassword(prevState => ({...prevState, id: !id}))
    };

    const validateForm = {
        checkUsernameFormat: () => {
            const usernameFormat = /^[\w-]+$/;
            return usernameFormat.test(inputs.username);
        },
        checkUsernameLength: () => {
            return inputs.username.length >= 6 && inputs.username.length <= 20;
        },
        checkEmailMatch: () => {
            return inputs.email === inputs.confirmEmail;
        },
        checkPasswordFormat: () => {
            return inputs.password.length >= 6 && inputs.password.length <= 20;
        },
        checkPasswordMatch: () => {
            return inputs.password === inputs.confirmPassword;
        }
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        alert(JSON.stringify(inputs));
    };

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({ ...values, [name]: value }))
    };

    return (
        <>
            <WebNav />
            <RegisterForm
                inputs={inputs}
                onSubmit={handleSubmit}
                onInputChange={handleChange}
                onValidation={validateForm}
                onToggle={togglePassword}
                showPassword={showPassword} />
            <Footer />
        </>
    )
};

export default Register;
