import React, { useState } from "react";
import { orderData } from '../data/cartData';
import { Trash } from 'react-bootstrap-icons';


const Cart = () => {
    const [orderList, setOrderList] = useState(orderData);

    return (
        <>
            <h1>Cart</h1>
            <div id="cart-container">
                <div id="product-info">
                    {
                        orderList.map((item, idx) => (
                            <div className="product-container border my-2">
                                <div>
                                    <img src={item.sku.img} alt={item.sku.itemName} />
                                </div>
                                <div>
                                    <h2>
                                        {item.title}
                                    </h2>
                                    <h3>
                                        {item.sku.itemName}
                                    </h3>
                                </div>
                                <div>
                                    {item.sku.price} <br />
                                    {item.sku.price * (100 - item.sku.discount) / 100}
                                </div>
                                <div>
                                    <button className="btn btn-primary btn-sm">-</button>
                                    <div className="d-inline-block mx-3 text-center">{item.amount}</div>
                                    <button className="btn btn-primary btn-sm">+</button>
                                </div>
                                <div>
                                    {item.sku.price * (100 - item.sku.discount) / 100 * item.amount}
                                </div>
                                <button className="btn btn-danger btn-sm"><Trash size={16} /></button>
                            </div>
                        ))
                    }
                </div>
                <div id="product-summary">
                    <h2>Total Price:</h2>
                    {
                        orderList.reduce((prevVal, currValue) => (
                            prevVal +
                            currValue.amount *
                            (currValue.sku.price * (100 - currValue.sku.discount) / 100)
                        ), 0)
                    }
                </div>
            </div>


        </>
    )
}

export default Cart;
