import React, { useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { Container, Col, Row, Button, Form, InputGroup, Badge, ListGroup } from 'react-bootstrap';
import { ArrowReturnLeft } from "react-bootstrap-icons";
import '../style/Order.css';

import { order } from "../data/order";

const OrderDetail = () => {
    const { userid } = useParams();
    const userOrder = order.filter(item => (item.id === userid))[0];
    console.log(userOrder);

    return (
        <Container id='order-detail-container' className="mt-4">
            <div className='row justify-content-center'>
                <h1 className="text-center">Order Details</h1>
                <Link to='/order' className="text-center mb-3"><ArrowReturnLeft className="me-2" />Back to Order Page</Link>
            </div>
            <Row>
                <h4 className="mb-3">
                    <span className="text-danger  me-3">Your cart</span>
                    <Badge pill bg="danger ">9</Badge>
                </h4>

                <ListGroup className="mb-3">
                    {
                        userOrder.product.map((item, key) => (
                            <ListGroup.Item className="lh-sm" key={key}>
                                <Col lg={5}>
                                    <Row>
                                        <Col sm={5} className="text-center">
                                            {item.code}
                                        </Col>

                                        <Col sm={7}>
                                            <h2>
                                                {item.title}
                                            </h2>
                                            <h3 className="text-secondary">
                                                {item.itemName}
                                            </h3>
                                        </Col>
                                    </Row>
                                </Col>
                            </ListGroup.Item>
                        ))
                    }
                    <ListGroup.Item className="d-flex justify-content-between">
                        <span>Total (HKD)</span>
                        <strong>$
                            {
                                userOrder.reduce((prevVal, currValue) => (
                                    prevVal +
                                    currValue.amount *
                                    (currValue.product.price * (100 - currValue.product.discount) / 100)
                                ), 0)
                            }
                        </strong>

                    </ListGroup.Item>
                </ListGroup>

            </Row>

        </Container>
    )
}

export default OrderDetail;
