import React from "react";
import { product } from '../data/product';
import { Link } from "react-router-dom";
import '../style/ProductList.css';

const ProductList = () => {
    return (
        <div id="prod-container" className="m-4 mx-auto d-flex justify-content-evenly flex-wrap border">
            {product.map((prod, idx) => (
                <Link to="/product" key={idx}>
                    <div className="border product-box m-2" >
                        <img src={prod.coverImg} alt={prod.title}
                            className="product-img d-block mx-auto" />
                        <p className="fw-bold text-center p-1">{prod.title}</p>
                    </div>
                </Link>
            ))}
        </div>
    )

}

export default ProductList;
