import React, { useReducer } from "react";
import { product } from '../data/product';

const initialCount = {count: 0};

const reducer = (state, action) => {
    switch (action.type) {
        case 'INCREMENT':
            return {count: state.count + 1};
        case "DECREMENT":
            return state.count > 0? {count: state.count - 1}: {count: 0};
        default:
            throw new Error();
    }
}

const ProductPage = () => {
    const noImagePath = '/img/no_image.svg';
    const [state, dispatch] = useReducer(reducer, initialCount);



    return (
        <div id="product-container" className="mt-4 mx-5 row">
            <div id='product-img' className="border border-secondary col-md-4">
                <img src={product.sku[0].img || noImagePath}
                    alt={product.sku[0].item} />
            </div>
            <div id='product-info' className="border border-secondary col-md">
                <h1>{product.title}</h1>
                {product.sku[1].discount > 0 ?
                    <h2><del className="me-2">${product.sku[1].price}</del>
                        ${product.sku[1].price * (100 - product.sku[1].discount) / 100}</h2> :
                    <h2>${product.sku[1].price}</h2>
                }

                <p>{product.description}</p>
                <div id="num-container">
                    <button className="btn btn-primary">-</button>
                    <span className="mx-2">{state.count}</span>
                    <button className="btn btn-primary">+</button>
                    <span className="mx-2 fw-bold">{product.sku[0].unit || 'Pieces'}</span>
                </div>
                <button className="btn btn-primary my-2">Add to Cart</button>

            </div>
        </div>
    )
}

export default ProductPage;
