import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Home from './pages/home';
import Login from './pages/login'
import Register from './pages/register';
import ProductPage from './pages/productPage';
import WebNav from "./component/WebNav";
import Footer from "./component/Footer";
import ProductList from "./pages/productList";
import SearchResult from "./pages/searchResult";
import Cart from "./pages/cart";
import CheckoutPage from "./pages/checkoutPage";
import PaymentSuccess from "./pages/actionSuccess";
import ModifyPassword from "./pages/modifyPassword";

const App = () => {
  return (
    <BrowserRouter>
      <div className="d-flex flex-column min-vh-100">
        <WebNav />
        <Routes>
          <Route exact path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/product/:goods" element={<ProductPage />} />
          <Route path="/category/:category" element={<ProductList />} />
          <Route path="/search/:keyword" element={<SearchResult />} />
          <Route path="/cart" element={<Cart />} />
          <Route path='/cart/checkout' element={<CheckoutPage />} />
          <Route path='/success' element={<ActionSuccess />} />
          <Route path='/login/forgot-password' element={<ModifyPassword />} />
        </Routes>
        <div className="mt-auto">
          <Footer />
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;
