import React, { useState } from "react";
import { orderData } from '../data/cartData';
import { Trash } from 'react-bootstrap-icons';


const Cart = () => {
    const [orderList, setOrderList] = useState(orderData);

    return (
        <>
            <h1>Cart</h1>
            {
                orderList.map((item, idx) => (
                    <div className="product-container border">
                        <div>
                            <img src={item.sku.img} alt={item.sku.itemName} />
                        </div>
                        <div>
                            <h2>
                                {item.title}
                            </h2>
                            <h3>
                                {item.sku.itemName}
                            </h3>
                        </div>
                        <div>
                            {item.sku.price} <br />
                            {item.sku.price * (100 - item.sku.discount) / 100}
                        </div>
                        <div>
                            <button className="btn btn-outline-primary btn-sm">-</button>
                            <div id="num-counter" className="d-inline-block mx-2 text-center">{item.amount}</div>
                            <button className="btn btn-outline-primary btn-sm">+</button>
                        </div>
                        <div>
                            {item.sku.price * item.amount}
                        </div>
                        <button className="btn btn-danger"><Trash /></button>
                    </div>
                ))
            }


        </>
    )
}

export default Cart;
