import React from "react";
import { Container, Row, Col, Badge, Card, Button, Form, InputGroup } from "react-bootstrap";
import { Formik } from 'formik';
import * as Yup from 'yup';
import '../style/Checkout.css';

const validationSchema = Yup.object().shape({
    name: Yup.string().required(),
    phone: Yup.string().required(),
    address: Yup.string().required(),
});

const CheckoutPage = () => {
    return (
        <>
            <Container id='checkout-container'>
                <div className="py-4 text-center">
                    <h2>Checkout form</h2>
                </div>

                <Row className="g-5">
                    <Col md={7}>
                        <Formik
                            initialValues={{
                                name: '',
                                phone: '',
                                address: '',
                                payment: '',
                                notes: '',
                            }}
                            onSubmit={(values) => {
                                alert(JSON.stringify(values, null, 2));
                            }}
                            validationSchema={validationSchema}
                        >
                            {
                                ({
                                    handleSubmit,
                                    handleChange,
                                    handleBlur,
                                    values,
                                    touched,
                                    isValid,
                                    errors,
                                }) => (
                                    <>
                                        <h2>Billing Address</h2>
                                        <Form id='checkout-form' noValidate onSubmit={handleSubmit}>
                                            <Row className="g-3">
                                                <Form.Group
                                                    as={Col}
                                                    md="7"
                                                    controlId="name"
                                                >
                                                    <Form.Label>Name</Form.Label>
                                                    <Form.Control
                                                        type="text"
                                                        name="name"
                                                        value={values.name}
                                                        onChange={handleChange}
                                                        isInvalid={!!errors.name}
                                                    />
                                                    <Form.Control.Feedback type="invalid" tooltip>Your name is missing.</Form.Control.Feedback>
                                                </Form.Group>

                                                
                                            <Form.Group as={Col} md="5" controlId="phone">
                                                <Form.Label>Phone Number</Form.Label>
                                                <InputGroup hasValidation>
                                                    <InputGroup.Text id="telCode">+852</InputGroup.Text>
                                                    <Form.Control
                                                        type="text"
                                                        name="name"
                                                        aria-describedby="telCode"
                                                        value={values.name}
                                                        onChange={handleChange}
                                                        isInvalid={!!errors.name}
                                                    />
                                                    <Form.Control.Feedback tooltip>
                                                        Phone number is missing.
                                                    </Form.Control.Feedback>
                                                </InputGroup>
                                            </Form.Group>

                                            </Row>




                                        </Form>
                                    </>

                                )
                            }

                        </Formik>
                    </Col>

                    <Col md={{ span: 5, order: "last" }}>
                        <h4 className="d-flex justify-content-between align-items-center mb-3">
                            <span className="text-danger">Your cart</span>
                            <Badge pill bg="danger">9</Badge>
                        </h4>

                        <ul className="list-group mb-3">
                            <li className="list-group-item d-flex justify-content-between lh-sm">
                                <div>
                                    <h6 className="my-0">Product name</h6>
                                    <small className="text-muted">Brief description</small>
                                </div>
                                <span className="text-muted">$12</span>
                            </li>
                        </ul>


                        <Card className="p-2">
                            <Button variant="primary" type="submit" form="checkout-form">Submit Order</Button>
                        </Card>
                    </Col>

                </Row>



            </Container>
        </>
    )
}

export default CheckoutPage;
