import React, { useState } from 'react';
import { Form, FloatingLabel, InputGroup, Button } from "react-bootstrap";
import { Formik } from 'formik';
import { XCircleFill } from "react-bootstrap-icons";
import PasswordVisible from "../component/PasswordVisible";
import * as Yup from 'yup';

const initialize = {
    user: '',
    newPassword: '',
    confirmPassword: '',
};

const schema = Yup.object().shape({
    user: Yup.string().required('Please enter username or email.'),
    newPassword: Yup.string()
        .max(20, 'Maximum 20 character')
        .min(6, 'Minimum 6 characters.')
        .required('Password is required'),
    confirmPassword: Yup.string()
        .oneOf([Yup.ref('password'), null], 'Password must be match')
});

const ModifyPassword = () => {

    const [showPassword, setShowPassword] = useState({
        newPwBtn: false,
        confirmNewPwBtn: false
    });

    const togglePassword = (id) => {
        setShowPassword(prevState => ({ ...prevState, [id]: !prevState[id] }))
    };


    return (
        <div id='password-form'>
            <Formik
                initialValues={initialize}
                validationSchema={schema}
                onSubmit={(values) => {
                    alert(JSON.stringify(values, null, 2));
                }}
            >
                {({ values, errors, touched, handleSubmit, handleChange, handleBlur }) => (
                    <Form>
                        <Form.Group className='mb-2'>
                            <Form.Label>Username or Email address</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="Username or Email Address"
                                name="user"
                                value={values.user}
                                onChange={handleChange}
                                onBlur={handleBlur}
                                isInvalid={touched.user && !!errors.user} />
                            <Form.Control.Feedback type="invalid" tooltip>
                                <XCircleFill className="me-2" />{errors.user}
                            </Form.Control.Feedback>
                        </Form.Group>

                        <InputGroup className='mb-2'>
                            <Form.Group controlId="new-password" label="Password" className="col-12">
                                <Form.Label>New Password</Form.Label>
                                <Form.Control
                                    type={showPassword.passwordBtn ? "text" : "password"}
                                    placeholder="Password"
                                    name='password'
                                    value={values.password}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    isInvalid={touched.password && !!errors.password}
                                />
                                <Form.Control.Feedback type="invalid" tooltip>
                                    <XCircleFill className="me-2" />{errors.password}
                                </Form.Control.Feedback>
                                <PasswordVisible
                                    id='toggle-pw-btn-1'
                                    name='passwordBtn'
                                    onToggle={togglePassword}
                                    showPassword={showPassword.passwordBtn}
                                />
                            </Form.Group>

                        </InputGroup>

                        <InputGroup className='mb-2'>
                            <Form.Group className="col-12">
                                <Form.Label>Confirm Password</Form.Label>
                                <Form.Control
                                    type={showPassword.confirmPwBtn ? "text" : "password"}
                                    placeholder="Confirm Password"
                                    name='confirmPassword'
                                    value={values.confirmPassword}
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    isInvalid={touched.confirmPassword && !!errors.confirmPassword}
                                />
                                <Form.Control.Feedback type="invalid" tooltip>
                                    <XCircleFill className="me-2" />{errors.confirmPassword}
                                </Form.Control.Feedback>
                            </Form.Group>
                            <PasswordVisible
                                id='toggle-pw-btn-2'
                                name='confirmPwBtn'
                                onToggle={togglePassword}
                                showPassword={showPassword.confirmPwBtn}
                            />
                        </InputGroup>

                    </Form>
                )}

            </Formik>
        </div>
    )
}

export default ModifyPassword;
