import React from 'react';
import { OverlayTrigger, Popover, Button, Form, FormControl } from 'react-bootstrap';
import { Search } from 'react-bootstrap-icons';

const MobileSearchBar = () => {
    return (
        <>
            <OverlayTrigger
                trigger="click"
                key='bottom'
                placement='bottom'
                overlay={
                    <Popover className='w-100 '>

                            <Form className="d-flex ms-auto me-auto p-3">
                                <FormControl
                                    type="search"
                                    placeholder="Search Product"
                                    className="me-2"
                                    aria-label="Search"
                                />
                                <Button variant="outline-light border border-3">
                                    <Search color="white" size={24} />
                                </Button>
                            </Form>
                    </Popover>
                }
            >
                <Button variant="secondary">Popover on bottom</Button>
            </OverlayTrigger>
        </>
    )
};

export default MobileSearchBar;
