import React from 'react';
import { Container, Row, Col, Button, Form } from 'react-bootstrap';
import { Trash, PlusLg } from "react-bootstrap-icons";
import { Formik, FieldArray } from 'formik';
import * as Yup from 'yup';
import '../style/ManageProduct.css'
import ProductForm from '../component/ProductForm';


const initialize = {
    code: '',
    title: '',
    description: '',
    coverImg: '',
    category: 'Rice',
    sku: [{
        itemName: '',
        price: 0,
        unit: '',
        inStock: 0,
        img: '',
        discount: 0,
    }]

}

const schema = Yup.object().shape({
    code: Yup.string().required('Product code is missing.'),
    title: Yup.string().required('Product title is missing.'),
    sku: Yup.array().of(
        Yup.object().shape({
            itemName: Yup.string().required('Item name is missing.'),
            price: Yup.number().min(0).required('Price is missing'),
            inStock: Yup.number().min(0).required('Stock number is missing.'),
            discount: Yup.number().min(0).max(100).required('Discount value is missing')
        })
    ).required('Items is missing')
})


const CreateProduct = () => {
    return (
        <Container id='create-prod-form' className='mb-5'>
            <div className="py-4 text-center">
                <h1>Create Product</h1>
            </div>
            <Row className='g-5'>
                <ProductForm initialize={initialize} for='Create'/>
            </Row>
        </Container >
    )

}

export default CreateProduct;
