import React, { useReducer, useState } from "react";
import { product } from '../data/product';
import '../style/Product.css'

const initialCount = { count: 0 };

const reducer = (state, action) => {
    switch (action.type) {
        case 'INCREMENT':
            return state.count < 99 ? { count: state.count + 1 } : { count: 99 };
        case "DECREMENT":
            return state.count > 0 ? { count: state.count - 1 } : { count: 0 };
        default:
            throw new Error();
    }
}

const ProductPage = () => {
    const noImagePath = '/img/no_image.svg';
    const [state, dispatch] = useReducer(reducer, initialCount);
    const [item, setItem] = useState(product.sku[0]);


    return (
        <div id="product-container" className="mt-4 mx-5 row">
            <div id='product-img' className="border border-secondary col-md-4 p-3">
                <img src={item.img || noImagePath}
                    alt={item.item} className="d-block mx-auto" />
            </div>
            <div id='product-info' className="border border-secondary col-md p-3">
                <h1>{product.title}</h1>
                {product.sku[0].discount > 0 ?
                    <h2><del className="me-2">${item.price}</del>
                        ${item.price * (100 - item.discount) / 100}</h2> :
                    <h2>${item.price}</h2>
                }
                <p>{product.description}</p>

                <div id="item-container" className="my-3">
                    {product.sku.map((item, index) => (
                        <button className="mx-3 item-btn" key={index} >
                            <img src={item.img || noImagePath}
                                alt={item.item} />
                        </button>
                    ))}

                </div>

                <div id="num-container">
                    <button className="btn btn-outline-primary btn-sm"
                        onClick={() => dispatch({ type: 'DECREMENT' })}>-</button>

                    <div id="num-counter" className="d-inline mx-2">{state.count}</div>

                    <button className="btn btn-outline-primary btn-sm"
                        onClick={() => dispatch({ type: 'INCREMENT' })}>+</button>

                    <span className="mx-2">{item.unit || 'Pieces'}</span>
                </div>
                <button className="btn btn-outline-primary my-2">Add to Cart</button>

            </div>
        </div>
    )
}

export default ProductPage;
